    private void btnConvertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConvertActionPerformed
        
        String num1 = txtNum1.getText();
        String num1rad = txtRad1.getText();
        String num2 = txtNum2.getText();
        String num2rad = txtRad2.getText();
        try
        {
            num2 = toString(toInt(num1,num1rad),num2rad); 
            txtNum2.setText(num2+"");
        }
        
        catch(Exception e)
        {
            txtError.setText("Invalid conversion: " + e.getMessage());
        }
  
    }//GEN-LAST:event_btnConvertActionPerformed

