    public int lookUpDigit(char n) throws Exception //method to look up digit from String
    {
        for(int i=0;i<arrayOfDigits.length;i++)
        {
            if(n==arrayOfDigits[i])
            {
                return i;
            }
        }
        throw new Exception("char: " + n + " is invalid"); //if there's no match, method throws Exception
      
    }
    
  
    public int toInt(String numberstring, String radix) throws Exception
    //takes in number and its radix in String form
    //converts to decimal number
    { 
        int numberradix = Integer.parseInt(radix); 
        if(numberradix<=1 || numberradix>16) //checking for validity of radix
            throw new Exception("Invalid radix: " + radix);
        
        int numberint = 0;
        
        System.out.println("Converting " + numberstring + " of radix: " + numberradix);
        
        for(int n=0;n<numberstring.length();n++)
        {
            numberint *= numberradix;
            numberint += lookUpDigit(numberstring.charAt(n));
        }
        
        return numberint;
    } 
    
    public String toString(int number, String radix) throws Exception
            //converts to desired radix
    {
        
        int numberradix = Integer.parseInt(radix);
        if(numberradix<=1 || numberradix>16) //checking for validity of radix
            throw new Exception("Invalid radix: " + radix);
        
        
        String converted = ""; //return value
        int y;
        while(number>0)
        {
            y = number%numberradix;
            converted = arrayOfDigits[y] + converted;
            number/=numberradix;
        }
        
        
        return converted;
    }
   
